<script>
import Header from '~/components/Header'
import PathList from '~/components/conf'
import appPathList from '~/components/conf-app'
import SZXJ from "~/components/vueHttp"
import Alert from '~/components/Alert'
import axios from 'axios'
import Footer from '~/components/Footer'
// this.$refs.alert.setMessage(false,'message',function(){})
export default {
	components:{
		'alert': Alert,
		'appPathList':appPathList
//		'myheader':Header,
//		'myfooter':Footer
	},
	asyncData:function(context){
		var obj={};
	    obj.id = context.params.id;
//		    console.log(context);
//		    console.log(obj.id);
		return obj;
	},
	data : function() {
		return {
			path: appPathList,
		    flag:false,
		    displayFlag:false,
		    sign:'',
		    obj:{
		      status:0,
		      pageNo:1,
		      pageSize:1,
		    },
		    objSec:{
		      status:1,
		      pageNo:1,
		      pageSize:1,
		    },
		    attention:[],
		    attentionSec:[],
		    queryUser:[],
		    unexchangeableCashAmount:'',
		    cardamount:'',
		    personal: true,
		    menuList: false,
		    toWrap: false,
		    length:0,
		    lengthSec:0
		}
	},
    mounted: function () { // ready -->
	  this.$nextTick(function () {
	  	this.getValueFn();
    	this.displayFlag = true;
    	k_touch("personal", "y");
	  })
	},
	methods: {
		showMenu: function() {
		    this.menuList = !this.menuList
		    this.toWrap = !this.toWrap;
		    if(this.menuList){
		        document.body.style.overflow = "hidden";
		    }else{
		        document.body.style.overflow = "auto";
		    };
		},
	    jumpFn:function(a){
	      if(this.flag){
	        window.location.href = appPathList.TemprootPath + '/app/' + a ; 
	      }
	    },
	    fansFn:function(a){
	      if(this.flag){
	        window.location.href = appPathList.TemprootPath + '/app/fans/' + a; 
	      }
	    },
	    backFn:function(){
	      history.go(-1);
	    },
	    signoutFn:function(){
	        this.flag = false;
	        var _data = {};
	        SZXJ.http(this,'get', appPathList.logout, {}, (response) => {
	        });
	        localStorage.removeItem('JSESSIONID');
	        location.href = appPathList.TemprootPath + '/app'; 
	    },
	    signFn: function() {  
	      if(this.flag){
	        SZXJ.http(this,'get', appPathList.cardsign, {}, (response) => {
	          this.sign = true;
	          SZXJ.http(this,'get', appPathList.cardamount, this.objSec, (response) => {
	            this.cardamount = response.data.amount.payCardAmount + response.data.amount.welfareCardAmount;
	          });
	        },(err)=>{
	          if( err.data.code !== 500){
	            this.sign = true;
	            SZXJ.http(this,'get', appPathList.cardamount, this.objSec, (response) => {
	              this.cardamount = response.data.amount.payCardAmount + response.data.amount.welfareCardAmount;
	            });
	          }
	        });
	        prompt("签到成功，获得好人卡10张");
	      }
	    },
	    getValueFn:function(){
	       var data = {};
	      SZXJ.http(this,'get', appPathList.getStatus, data, (response) => {
	        this.sign = response.data.status.sign;
	        this.flag = response.data.status.flag;
	      });
	      SZXJ.http(this,'post', appPathList.queryUser, data, (response) => {
	          this.queryUser = response.data;
	      },(err)=>{location.href='/app/login'});
	      SZXJ.http(this,'get', appPathList.myCareOrCareMe, this.obj, (response) => {
	          this.length = response.data.attention.length;
	      });
	      SZXJ.http(this,'get', appPathList.myCareOrCareMe, this.objSec, (response) => {
	          this.lengthSec = response.data.attention.length;
	      });
	      SZXJ.http(this,'get', appPathList.amount, this.objSec, (response) => {
	          this.unexchangeableCashAmount = response.data.amount.unexchangeableCashAmount+response.data.amount.exchangeableCashAmount;
	      });
	      SZXJ.http(this,'get', appPathList.cardamount, this.objSec, (response) => {
	          this.cardamount = response.data.amount.payCardAmount + response.data.amount.welfareCardAmount;
	      });
	      this.displayFlag = true;
	      this.$nextTick(function(){
		       this.displayFlag = true;
		       var  This = this;
		       if(document.getElementById("loading")){
		           setTimeout(function(){
		            document.body.removeChild(document.getElementById("loading"));
		            },300)
		       }
		    });
	    }
	},
  	head: function(){
  		return {
  			title: '个人中心-QC轻小说',
  	    meta: [
  			    { charset: 'UTF-8' },
  			    { name: 'viewport', content: 'width=device-width, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0,user-scalable=no' },
  			  	{ name: 'format-detection', content: 'telephone=no' },
