<template>
<div id="fme">
	<script src="/lib/resize_320.js"></script>
	<!--<div class="tp"></div>-->
	<div class="alls">
		<!--<div class="top">
			<img @click="backFn" class="back" src="/img/back.png" alt="" />
			<span>关注我的</span>
		</div>-->
		<div class="cont" v-if="record.length > 0">
			<div class="secord" v-for="obj in record">
				<img class="pic" :src="path.imagePath + obj.userHead" alt="" onerror="javascript:this.src='data:image/jpeg;base64,';" />
				<span class="user">{{obj.userName}}</span>
				<span class="per">{{obj.information}}</span>
				<img v-if="obj.attention" class="pic2" @click="folFn('')" src="http://cdn.qcacg.com/mobile/img/addto.png" />
				<img v-else class="pic2" @click="folFn(obj.userId)" src="http://cdn.qcacg.com/mobile/img/addto.png" />
			</div>
		</div>
		<div class="con" v-else>
			<span class="pro">亲，还未有人关注关注您哟</span>
		</div>
	</div>
</div>
</template>

<script>
import appPathList from '~/components/conf-app'
import SZXJ from '~/components/vueHttp'
export default{
	components:{
		
	},
	data: function(){
		return{
			path:appPathList,
			szxj:SZXJ,
			record:[],
			obj:{
				status:1,
				pageNo:1,
				pageSize:100,
			},
			ws:{},
			wn:{},
		}
	},
	mounted: function(){
		this.$nextTick(function(){
			this.getValueFn();
			if(window.plus){
	            this.plusReady();
		    }else{
		        document.addEventListener('plusready', this.plusReady, false);
		    }
		})
	},
	methods: {
		backFn:function(){
		      if(window.plus){
		        this.webviewAnimation();
		      }else{
		        history.back()
		      }
	    },
	    plusReady:function(){
	        var self = this;
	        this.ws = plus.webview.currentWebview();
	        this.ws.overrideUrlLoading({mode:'reject'}, function(e){ //拦截跳往其他url
		        console.log('reject url: '+e.url);
		    });
	        plus.key.addEventListener('backbutton', function(){
				self.ws.close("slide-out-right");
	//			self.ws.opener().reload();  //webview重新加载
			}, false);
	    },
	    webviewAnimation:function(){
	      this.ws.hide('slide-out-right');
	    },
		folFn: function(a){
			if(a != ''){
				SZXJ.http(this,'post',appPathList.saveOrCancelAttention,{receiveId: a},(response)=>{
					this.getValueFn();
					if(window.plus){
		    			plus.nativeUI.toast( "已成功关注");
		    		}
	//					location.href = '/mView/folme';
				})
			}else{
				if(window.plus){
	    			plus.nativeUI.toast( "你已关注过他");
	    		}
			}
		},
		getValueFn: function(){
			SZXJ.http(this,'get',appPathList.myCareOrCareMe,this.obj,(response)=>{
				this.record = response.data.attention;
				console.log(response);
			})
		},
	},
	head: function(){
		return{
			title:'关注我的-QC轻小说',
			meta:[
				{charset:'UTF-8'},
				{ name: 'apple-touch-fullscreen', content: 'yes' },
		        { name: 'viewport', content: 'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0' },
		        { name: 'format-detection', content: 'telephone=no' },
		        { name: "apple-mobile-web-app-capable",content:"yes"},
		        { name: "apple-mobile-web-app-status-bar-style",content:"default"},
		        { name: "description",content:"轻小说关注我的"},
		        { name: "keywords",content:"轻小说关注我的"},
			],
			link:[],
		}
	},
}
</script>

