	#fme .alls .top:after{height: .0072rem;left: 0;right: 0;bottom: 0;position: absolute;content: '';background: #C3C3C3;}
	#fme .alls .top .back{position: absolute;left: .128rem;width: .085rem;height: .1536rem;top: .1109rem;}
	#fme .alls .top span{font-family: "微软雅黑";font-size: .145rem;height: .376rem;padding: .116rem 0;text-align: center;box-sizing: border-box;display: block;}
	#fme .alls .cont{width: 100%;margin: 0 auto;position: absolute;/*top: .547rem;*/top: 0;}
	#fme .alls .cont .secord{width: 100%;height: .376rem;margin-top: 1px;background-color: white;}
	#fme .alls .cont .secord .pic{position: relative;width: .256rem;height: .256rem;left: .128rem;top: .06rem;border-radius: 5rem;background-color:#00A1D6;}
	#fme .alls .cont .secord .user{position: absolute;color: #C3C3C3;font-family: "微软雅黑";font-size: .094rem;margin-top: .055rem;margin-left: .235rem;}
	#fme .alls .cont .secord .per{position: absolute;font-family: "微软雅黑";font-size: .1024rem;margin-top: .222rem;margin-left: .235rem;overflow: hidden;text-overflow: ellipsis;white-space: nowrap;max-width: 1.8rem;}
	#fme .alls .cont .secord .pic2{float: right;margin-right: .128rem;width: .205rem;height: .222rem;margin-top: .077rem;}
	#fme .alls .con{width: 100%;position: absolute;top: .378rem;}
	#fme .alls .con .pro{text-align: center;margin-top: 20%;display: block;font-size: .128rem;}
</style>