	        this.loginFlag = response.data.loginFlag;
	      });
	      SZXJ.http(this,'get', PathList.queryCanContribution, {userId:this.id}, (response) => {
	        this.isSolicitContributions = response.data.isSolicitContributions;
	      });
		  SZXJ.http(this,'get', PathList.queryPaintList, {userId:this.id}, (response) => {
		  		this.whole = response.data;
			    this.$refs.page.setPageCount(response.data.paintListData.pages);
		        this.paintList = response.data.paintListData.list;
		        this.paintNum = response.data.paintNumData;
