<script>
import Header from '~/components/Header'
import PathList from '~/components/conf'
import appPathList from '~/components/conf-app'
import SZXJ from '~/components/vueHttp'
import Alert from '~/components/Alert'
import axios from 'axios'
import Footer from '~/components/Footer'
import pageComponent from '~/components/pageComponent'
// this.$refs.alert.setMessage(false,'message',function(){})
export default {
	components:{
		'alert': Alert,
		'myheader':Header,
		'myfooter':Footer,
		'pageComponent':pageComponent
	},
	asyncData:function(context){
		var obj={};
	    obj.id =context.params.id;
		return obj;
	},
	data : function() {
		return {
			path:PathList,
		    classTypeList:[],
		    styleTypeList:[],
		    listAlbum:[],
		    obj:{
		      pageNum:1,
		      pageSize:10,
		      sort:0,
		      classType:'',
		      styleType:'',
		      keyword:'', 
		    },
		    flag:1,
		    className:'不限',
		    styleName:'不限',
		    keywords:'',
		    numBer:'',
		    offsetHeight:'',
		    clientHeight:'',
		    srcollTop:'',
		    srcollH:'',
		    pages:'',
		    pageNum:'',
		}
	},
    mounted: function () { // ready -->
	    this.$nextTick(function () {
//	  		if(SZXJ.getQueryString('key')){
//		       this.searchKeyFn(SZXJ.getQueryString('key'));
//		    }
//          this.searchKeyFn(this.id);
			if(this.id !=undefined){
				this.searchKeyFn(this.id);
			}else if(this.id == ''){
				this.searchKeyFn();
			}
		    this.getValueFn();
		})
	},
	methods: {
		getValueFn:function(){ // 默认请求
		    SZXJ.http(this,'get', PathList.typeList, { typeType: 1 }, (response) => {
                this.classTypeList = response.data.data;
		    });
		    SZXJ.http(this,'get', PathList.typeList, { typeType: 2 }, (response) => { 
		        this.styleTypeList = response.data.data;
		    });
		    this.getAlbumByFilter();
		    var self = this;
		    this.$refs.page.setFn(function(){
		    	this.$emit('update:pageNum', this.pageNum);
		    	this.$emit('update:pageSize', this.pageSize);
	    		self.getAlbumByFilter();
	    	});
	    },
	    searchKeyFn:function(a){ // 搜索关键字
	    	if(a !=''){
			    this.obj.keyword = a;
			    this.getAlbumByFilter();
	    	}else{
	    		this.obj.keyword = a;
			    this.getAlbumByFilter();
	    	}
	    },
	    setWorksFn:function(a,b){ // 最新作品
		    this.flag = a ;
		    this.obj.sort = b;
		    this.getAlbumByFilter();
	    },
	    setClassWorksFn:function(a,b){ // 设置风格
		    this.className = b;
		    this.obj.classType = a;
		    this.getAlbumByFilter();
	    },
	    setStyleWorksFn:function(a,b){ // 设置类型
		    this.obj.styleType = a;
		    this.styleName = b;
		    this.getAlbumByFilter();
	    },
	    getAlbumByFilter:function(){
	    	SZXJ.http(this,'get', PathList.listAlbumByFilter, this.obj, (response) => {
		        this.listAlbum = response.data.data;
		        this.$refs.page.setPageCount(response.data.data.pages);
		    });
	    },
	    setNewLaudAmountFn:function(a,b){ // 增加点赞数目
		    SZXJ.http(this,'get', PathList.giveLaud,{albumId:a}, (response) => {
		        this.listAlbum.list[b].laudAmount += 1;
		        this.listAlbum.list[b].hasLaud = 1;
		    },(err)=>{
		    	this.$refs.alert.setMessage(true, err.body.msg,function(){
		      		
		      	});
		    });
	    }
	},
  	head: function(){
		return {
			title: '作品页-QC轻小说',
	    	meta: [
			    { charset: 'UTF-8' },
			    { name: 'viewport', content: 'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0' },
			  	{ name: 'format-detection', content: 'telephone=no' },
			  	{ name: 'apple-touch-fullscreen', content: 'yes' },
			  	{ name: 'apple-mobile-web-app-capable', content: 'yes' },
			  	{ name: 'apple-mobile-web-app-status-bar-style', content: 'default' },
			  	{ name: 'keywords', content: 'QC,QCACG,QC轻小说,轻小说,轻文,原创轻小说,轻小说投稿' },
			  	{ name: 'description', content: 'QC原创轻小说，一个在二次元盛起的魔法圣殿，拥有魔幻与神奇色彩，为二次元提供轻小说投稿与画师约稿相结合的平台。加入我们，接受契约，用轻松奇幻的文字留下你心中的故事。 ' },
			  	{ name: 'baidu_union_verify', content: '1dbb2d68c26ba734e502097619803d73' }
			],
			link: [
		    	{ rel: 'stylesheet', href: '/painter/css/main_works.css' },
		    	{ rel: 'stylesheet', href: '/css/headerOrFooter.css' },
		    	
	  		]
	  	}
  	}
}

</script>

